# Projeto de Estrutura de Dadosdef Program2():    Clientes = ["Joao"]    cont = 0    # Imprimir Menu            def Menu():        print(" \n\n------------------ Menu ------------------")        print(" 1 - Usuário Comum  ")        print(" 2 - Gestor de Recurso")        print(" 3 - Coordenador")        print(" 4 - Login")        print(" 0 - Sair do programa\n")        # Verificação e adição de item    #Usuário Comum    def UsuárioComum():        print(" Vamos fazer o Cadastro")        NovoCliente = input(" Digite seu NOME: ")        NovoCliente = input("Digite sua MATRICULA: ")        NovoCliente = input("Digite sua SENHA: ")        #indice = Clientes.index(NovoCliente)        if (NovoCliente in Clientes):            print("Esse clinte já esta cadastrado!")        else:            Clientes.append(NovoCliente)            print("Cliente cadastrado!")    def Login():        print('Você está Acessando sua Conta')        login = input(" Digite seu NOME: ")        login = input("Digite sua SENHA: ")        if (login in Clientes):            print('Você está Logado')        else:            print('Login não Encontrado! ')    # Gestor de Recurso    def GestorRecurso():        print(" Exibir lista de Recurso:")        #Clientes.soft()        for I in (Clientes):            print(I)        print('\nConfirmação do Local de Reuniões')        local =  input("Você vai Participar da Reunião? : \n\n")        print('Cadastrar o Novo Espaço de Reuniões:')        novoLocal =  input(" Qual Local Você deseja? ")        print(local, novoLocal)    #Faltar colocar o COORDENADOR    # Função remover da lista    def Cancelamento():        print(" Fazendo cancelamento do Cadastro")        NovoCliente = input(" Digite o nome do cliente: ")        if (NovoCliente in Clientes):            Clientes.remove(NovoCliente)        else:            print(" Cliente não cadastrado!")                # Funçao de sair do menu    def Sair():        print("Saindo do programa!!")    while (cont != 4):        Menu()        cont = int(input(" Digite a Opção Você Deseja: "))                if (cont == 1):            UsuárioComum()        elif (cont == 2):            Cancelamento()        elif (cont == 3):            GestorRecurso()        elif (cont == 4):            Login()        elif (cont == 0):            Sair()Program2()